HassGetState.one_yesno:
{% if query.matched %}
  Ja
{% else %}
  Nee, {{ state.state_with_unit }}
{% endif %}

HassGetState.any:
{% if query.matched %}
  {% set match = query.matched | map(attribute="name") | sort | list %}
    {% if match | length > 4 %}
      Ja, {{ match[:3] | join(", ") }} en nog {{ (match | length - 3) }}
    {%- else -%}
      Ja,
      {% for name in match -%}
        {% if not loop.first and not loop.last %}, {% elif loop.last and not loop.first %} en {% endif -%}
        {{ name }}
      {%- endfor -%}
  {% endif %}
{% else %}
  Nee
{% endif %}

HassGetState.all:
{% if not query.unmatched: %}
  Ja
{% else %}
  {% set no_match = query.unmatched | map(attribute="name") | sort | list %}
    {% if no_match | length > 4 %}
      Nee, {{ no_match[:3] | join(", ") }} en nog {{ (no_match | length - 3) }} nie
    {%- else -%}
      Nee,
      {% for name in no_match -%}
        {% if not loop.first and not loop.last %}, {% elif loop.last and not loop.first %} en {% endif -%}
        {{ name }}
      {%- endfor
  %} not
    {% endif %}
{% endif %}

