HassGetState.one_yesno:
{% if query.matched %}
  네
{% else %}
  아니요, {{ slots.name }}의 상태는 {{ state.state }}입니다
{% endif %}

HassGetState.any:
{% if query.matched %}
  {% set match = query.matched | map(attribute="name") | sort | list %}
  {% if match | length > 4 %}
    네, {{ match[:3] | join(", ") }} 그리고 {{ (match | length - 3) }}개의 기기가 그렇습니다
  {%- else -%}
    {% if match | length == 1 %}
      네, {{ match|first }} 기기가 그렇습니다
    {% else %}
      네,
      {% for name in match -%}
        {% if not loop.first and not loop.last %}, {% elif loop.last and not loop.first %} 그리고 {% endif -%}
        {{ name }}
      {%- endfor -%}
      기기가 그렇습니다
    {% endif %}
  {% endif %}
{% else %}
  아니요
{% endif %}

HassGetState.all:
{% if not query.unmatched: %}
  네
{% else %}
  {% set no_match = query.unmatched | map(attribute="name") | sort | list %}
  {% if no_match | length > 4 %}
    아니요, {{ no_match[:3] | join(", ") }} 그리고 {{ (no_match | length - 3) }}개의 기기는 그렇지 않습니다
  {% else %}
    {% if no_match | length == 1 %}
      아니요, {{ no_match|first }} 기기는 그렇지 않습니다
    {%- else -%}
      아니요,
      {% for name in no_match -%}
        {% if not loop.first and not loop.last %}, {% elif loop.last and not loop.first %} 그리고 {% endif -%}
        {{ name }}
      {%- endfor %}
      기기가 그렇습니다
    {% endif %}
  {% endif %}
{% endif %}

HassGetState.which:
{% if not query.matched %}
  어떤 기기도 그렇지 않습니다
{% else: %}
  {% set match = query.matched | map(attribute="name") | sort | list %}
  {% if match | length > 4 %}
    {{ match[:3] | join(", ") }} 그리고 {{ (match | length - 3) }}개의 기기가 그렇습니다
  {% else %}
    {%- for name in match -%}
      {% if not loop.first and not loop.last %}, {% elif loop.last and not loop.first %} 그리고 {% endif -%}
      {{ name }}
    {%- endfor %}
    기기가 그렇습니다
  {% endif %}
{% endif %}
